mapper.select:
select sc.*,pp.ProductInventory,pi.ImageSite,pb.ProductName from shoppingCart sc LEFT JOIN ProductPrice pp ON sc.productId=pp.ProductId LEFT JOIN ProductImage pi ON pp.ProductId = pi.ProductId LEFT JOIN ProductBasis pb ON pi.ProductId = pb.ProductId
AND sc.userId=#{userId}
AND sc.shoppingCartId=#{shoppingCartId}
AND pi.sign=1 order by sc.shoppingCartId desc

mapper.delete:
DELETE FROM shoppingCart
WHERE shoppingCartId IN

    #{shoppingCartId}
